UPDATE result_fields rf
SET
  category = ld.category,
  comments = ld.comments,
  source = ld.source,
  analysis_metadata = jsonb_set(COALESCE(rf.analysis_metadata, '{}'), '{geeAssets}', ld.gee_assets)
FROM layer_data ld
WHERE rf.code = ld.code;
